using System;
using ModelFramework;

public class Script 
{      
   [Link] Paddock MyPaddock; // Can be used to dynamically get access to simulation structure and variables
   [Input] DateTime Today;   // Equates to the value of the current simulation date - value comes from CLOCK
   [Param] string A;         // The value for this will come from the Properties page.
   [Output] double B;        // An example of how to make a variable available to other APSIM modules


   // The following event handler will be called once at the beginning of the simulation
   [EventHandler] public void OnInitialised()
   {

   }

   // The following event handler will be called each day at the beginning of the day
   [EventHandler] public void OnPrepare()
   {

   }

}

using System;
using ModelFramework;
using CSGeneral;

// Basic crop management: sowing & harvesting.
// Multi-paddock aware.
// This component DOES NOT require a trigger from the sequencer.
// It will assume it is the only crop in the system if it doesnt find a sequencer.
// If it does find a sequencer, it will do nothing until told to (via Enter/Leave).
public class Script 
{      
   [Link()]  public Simulation MySimulation;
   
   [Param()] private string crop;         // The module name of this crop
   [Param()] private string date1;         //Start of sowing window
   [Param()] private string date2;         //End of sowing window
   [Param()] private string date3;         //date to change to different cultivar
   [Param()] int esw_amount;
   [Param()] private string must_sow;

   [Input()] private DateTime today;

   // Rainfall accumulator
   [Param()] int rain_days;              //check for rain over this period
   [Param()] int rain_amount;            //this much rain over that period

   // Daily rainfall from the system
   [Input] private double rain;
   private ManagerUtility.Tracker<double> rainTracker; 

   private bool inWindow = false;
   private bool endOfWindow = false;
   private bool ChangeCultivar = false;

   //initialise tracker, telling it how many days to track
   [EventHandler] public void OnInitialised()
   {
      rainTracker = new ManagerUtility.Tracker<double>(rain_days); 
   }

   // Daily tests common to all paddocks
   [EventHandler] public void OnPrepare()
   {
      bool startOfWindow = DateUtility.DatesEqual(date1, today);

      inWindow = DateUtility.WithinDates(date1, today, date2);
      ChangeCultivar = false;
      if(date3 != "na")  
         ChangeCultivar = DateUtility.WithinDates(date3, today, date2);
      endOfWindow = DateUtility.DatesEqual(date2, today);

      rainTracker.Add(rain);
      string currentPaddock = "";
      if (MySimulation.Get("currentPaddock", out currentPaddock) == false) 
      {
         // If there is no sequencer plugged in then we are it..
         if (canEnter > 0) {
            OnEnter();
         }
         if (canLeave > 0) {
            OnLeave();
         }
      }
   }
   // Test whether we can sow a crop today
   // +ve number - yes
   // 0          - no
   // -ve number - no, out of scope (planting window)
   [Output, Description("Test whether we can sow a crop today")] public int canEnter  {
      get {
         bool isPossibleToday = false;
         
         string currentPaddock;
         MySimulation.Get("currentPaddock", out currentPaddock);
         //Console.WriteLine("1. '" + currentPaddock + "'");

         Component paddockModule;
         if (currentPaddock != "")
            paddockModule = (Component) MySimulation.LinkByName(currentPaddock);
         else
            paddockModule = (Component) MySimulation.ChildPaddocks[0];
         //Console.WriteLine("2. " + paddockModule.Name);

         Component cropModule = (Component) paddockModule.LinkByName( crop );

         //Console.WriteLine("3. " + cropModule.Name);
         string plantStatus = "";
         cropModule.Get("plant_status", out plantStatus);

         double esw = 0.0;
         Component soilModule = (Component) paddockModule.LinkByType("SoilWat");
         soilModule.Get("esw", out esw);
         if (plantStatus == "out" &&
             inWindow &&
             rainTracker.Sum() >= rain_amount &&
             esw > esw_amount) 
         {
             isPossibleToday = true;
         } 

      if (isPossibleToday)
            return 1;
         
         if (plantStatus == "out" && endOfWindow && must_sow == "yes")
            return 1;

         if (plantStatus == "out" && !inWindow)
            return -1;
         
         return 0;
      }
   }  
   
   [Output, Description("Test whether we have passed the end of the window")] public int pastWindow  {
      get {
         return( (DateUtility.CompareDates(date2, today) < 0) ? 1 : 0);
      }
   }
   
   // Sow a crop
   [Param()] private string cultivar1;
   [Param()] private string cultivar2;
   [Param()] private double density1;
   [Param()] private double depth1;
   [Param()] private double row_spacing1;
   [Param()] private string ftn1;
   [Param()] private string skiprow1;
   [Param()] private string tillageImplement;
   
   [EventHandler, Description("Sow the crop")] public void OnEnter()
   {
      Console.WriteLine(today + " Sowing Crop");
      SowType data = new SowType();
      data.Cultivar = cultivar1;
      if(ChangeCultivar)
         data.Cultivar = cultivar2;

      data.plants = density1;
      data.sowing_depth = depth1;
      data.row_spacing = row_spacing1;
      data.tiller_no_fertile = (ftn1 == "na") ? "" : ftn1;
      data.SkipRow = 0 ;
      if (skiprow1 == "single")
         data.SkipRow = 1;
      else if (skiprow1 == "double")
         data.SkipRow = 2;

      string currentPaddock;
      MySimulation.Get("currentPaddock", out currentPaddock);
      Component cropModule;
      if (currentPaddock != "")
         cropModule = (Component) MySimulation.LinkByName(currentPaddock + "." + crop);
      else 
         cropModule = (Component) MySimulation.ChildPaddocks[0].LinkByName(crop);

      cropModule.Publish("Sow", data);
      if (tillageImplement.ToLower() != "na") 
   {
         TillageType t = new TillageType();
         t.type = tillageImplement;
         Component paddockModule;
         if (currentPaddock != "")
            paddockModule = (Component) MySimulation.LinkByName(currentPaddock);
         else
            paddockModule = MySimulation.ChildPaddocks[0];
         paddockModule.Publish("tillage", t);
      }   
   }

   // Test whether we can harvest a crop today
   // +ve number - yes
   // 0          - no
   // -ve        - out of scope
   [Output] public int canLeave  
   {
      get 
      {
         string currentPaddock;
         MySimulation.Get("currentPaddock", out currentPaddock);

         string plantStatus = "";
         MySimulation.Get((currentPaddock != "" ? currentPaddock + "." : "") + crop + ".plant_status", out plantStatus);
         if (plantStatus == "out")
            return -1;

         string StageName = "";
         MySimulation.Get((currentPaddock != "" ? currentPaddock + "." : "") + crop + ".StageName", out StageName);
         if (StageName == "harvest_ripe" || plantStatus == "dead")
            return 1;
         return 0;
      }
   }

   [EventHandler] public void OnLeave()
   {
      Console.WriteLine(today + " Harvesting Crop");
      HarvestType hdata = new HarvestType();
      hdata.Remove = 0.0;
      string currentPaddock;
      MySimulation.Get("currentPaddock", out currentPaddock);
      Component cropModule;
      if (currentPaddock != "")
         cropModule = (Component) MySimulation.LinkByName(currentPaddock + "." + crop);
      else 
         cropModule = (Component) MySimulation.ChildPaddocks[0].LinkByName(crop);

      cropModule.Publish("harvest", hdata);

      KillCropType kdata = new KillCropType();
      kdata.KillFraction = 0.0F;
      cropModule.Publish("killcrop", kdata);
      cropModule.Publish("end_crop");
   } 
}

using System;
using System.Collections.Generic;
using ModelFramework;
using CSGeneral;

// Basic crop management: sowing & harvesting.
// Multi-paddock aware.
// This component DOES NOT require a trigger from the sequencer.
// It will assume it is the only crop in the system if it doesnt find a sequencer.
// If it does find a sequencer, it will do nothing until told to (via Enter/Leave).
public class Script 
{      
   [Link()]  public Simulation MySimulation;
   
   [Param()] private string crop;         // The module name of this crop
   [Param()] private string date1;         //Start of sowing window
   [Param()] private string date2;         //End of sowing window
   [Param()] private string date3;         //date to change to different cultivar
   [Param()] int esw_amount;
   [Param()] private string must_sow;

   [Input()] private DateTime today;

   // Rainfall accumulator
   [Param()] int rain_days;              //check for rain over this period
   [Param()] int rain_amount;            //this much rain over that period

   // Day tracker
   private Dictionary<string,int> daysIn = new Dictionary<string,int>();
   [Param] float yearsInLey;            // How many years to remain in ley phase
  
   // Daily rainfall from the system
   [Input] private double rain;
   private ManagerUtility.Tracker<double> rainTracker; 

   private bool inWindow = false;
   private bool endOfWindow = false;
   private bool ChangeCultivar = false;

   //initialise tracker, telling it how many days to track
   [EventHandler] public void OnStart_Simulation()
   {
      rainTracker = new ManagerUtility.Tracker<double>(rain_days); 
      daysIn.Add("", -1);
      foreach (Paddock paddock in MySimulation.ChildPaddocks) 
         daysIn.Add(paddock.Name, -1);

   }

   // Daily tests common to all paddocks
   [EventHandler] public void OnPrepare()
   {
      bool startOfWindow = DateUtility.DatesEqual(date1, today);

      inWindow = DateUtility.WithinDates(date1, today, date2);
      ChangeCultivar = false;
      if(date3 != "na")  
         ChangeCultivar = DateUtility.WithinDates(date3, today, date2);
      endOfWindow = DateUtility.DatesEqual(date2, today);

      rainTracker.Add(rain);
      string currentPaddock = "";
      if (MySimulation.Get("currentPaddock", out currentPaddock) == false) 
      {
         // If there is no sequencer plugged in then we are it..
         if (canEnter > 0) {
            OnEnter();
         }
         if (canLeave > 0) {
            OnLeave();
         }
      }
   }
   [EventHandler] public void OnPost()
   {
      List <string> paddocks = new List<string>(daysIn.Keys);
      foreach (string p in paddocks) 
         if (daysIn[p] >= 0)
            daysIn[p]++;
   }
   // Test whether we can sow a crop today
   // +ve number - yes
   // 0          - no
   // -ve number - no, out of scope (planting window)
   [Output, Description("Test whether we can sow a crop today")] public int canEnter  {
      get {
         bool isPossibleToday = false;
         
         string currentPaddock;
         MySimulation.Get("currentPaddock", out currentPaddock);
         //Console.WriteLine("1. '" + currentPaddock + "'");

         Component paddockModule;
         if (currentPaddock != "")
            paddockModule = (Component) MySimulation.LinkByName(currentPaddock);
         else
            paddockModule = (Component) MySimulation.ChildPaddocks[0];
         //Console.WriteLine("2. " + paddockModule.Name);

         Component cropModule = (Component) paddockModule.LinkByName( crop );

         //Console.WriteLine("3. " + cropModule.Name);
         string plantStatus = "";
         cropModule.Get("plant_status", out plantStatus);

         double esw = 0.0;
         Component soilModule = (Component) paddockModule.LinkByType("SoilWat");
         soilModule.Get("esw", out esw);
         if (plantStatus == "out" &&
             inWindow &&
             rainTracker.Sum() >= rain_amount &&
             esw > esw_amount) 
         {
             isPossibleToday = true;
         } 

	     if (isPossibleToday)
            return 1;
         
         if (plantStatus == "out" && endOfWindow && must_sow == "yes")
            return 1;

         if (plantStatus == "out" && !inWindow)
            return -1;
         
         return 0;
      }
   }  
   [Output, Description("Test whether we have passed the end of the window")] public int pastWindow  {
      get {
         return( (DateUtility.CompareDates(date2, today) < 0) ? 1 : 0);
      }
   }
   
   // Sow a crop
   [Param()] private string cultivar1;
   [Param()] private string cultivar2;
   [Param()] private double density1;
   [Param()] private double depth1;
   [Param()] private double row_spacing1;
   [Param()] private string ftn1;
   [Param()] private string skiprow1;
   [Param()] private string tillageImplement;
   
   [EventHandler, Description("Sow the crop")] public void OnEnter()
   {
      Console.WriteLine(today + " Sowing Crop");
      SowType data = new SowType();
      data.Cultivar = cultivar1;
      if(ChangeCultivar)
         data.Cultivar = cultivar2;

      data.plants = density1;
      data.sowing_depth = depth1;
      data.row_spacing = row_spacing1;
      data.tiller_no_fertile = (ftn1 == "na") ? "" : ftn1;
      data.SkipRow = 0 ;
      if (skiprow1 == "single")
         data.SkipRow = 1;
      else if (skiprow1 == "double")
         data.SkipRow = 2;

      string currentPaddock;
      MySimulation.Get("currentPaddock", out currentPaddock);
      Component cropModule;
      if (currentPaddock != "")
         cropModule = (Component) MySimulation.LinkByName(currentPaddock + "." + crop);
      else 
         cropModule = (Component) MySimulation.ChildPaddocks[0].LinkByName(crop);

      cropModule.Publish("Sow", data);
      if (tillageImplement.ToLower() != "na") 
	  {
         TillageType t = new TillageType();
         t.type = tillageImplement;
         Component paddockModule;
         if (currentPaddock != "")
            paddockModule = (Component) MySimulation.LinkByName(currentPaddock);
         else
            paddockModule = MySimulation.ChildPaddocks[0];
         paddockModule.Publish("tillage", t);
      }		 
      daysIn[currentPaddock] = 0;
   }

   // Test whether we can harvest a crop today
   // +ve number - yes
   // 0          - no
   // -ve        - out of scope
   [Output, Description("Test whether the ley has finished")] public int canLeave  
   {
      get 
      {
         string currentPaddock;
         MySimulation.Get("currentPaddock", out currentPaddock);

         string plantStatus = "";
         MySimulation.Get((currentPaddock != "" ? currentPaddock + "." : "") + crop + ".plant_status", out plantStatus);
         if (plantStatus == "out")
            return -1;

         string StageName = "";
         MySimulation.Get((currentPaddock != "" ? currentPaddock + "." : "") + crop + ".StageName", out StageName);
         if (plantStatus == "dead")
            return 1;
         
         if (daysIn[currentPaddock] > /*(int) (*/365.25 * yearsInLey)
            return 1;
         
         return 0;
      }
   }

   [EventHandler] public void OnLeave()
   {
      Console.WriteLine(today + " Harvesting Crop");
      HarvestType hdata = new HarvestType();
      hdata.Remove = 0.0;
      string currentPaddock;
      MySimulation.Get("currentPaddock", out currentPaddock);
      Component cropModule;
      if (currentPaddock != "")
         cropModule = (Component) MySimulation.LinkByName(currentPaddock + "." + crop);
      else 
         cropModule = (Component) MySimulation.ChildPaddocks[0].LinkByName(crop);

      cropModule.Publish("harvest", hdata);

      KillCropType kdata = new KillCropType();
      kdata.KillFraction = 0.0F;
      cropModule.Publish("killcrop", kdata);
      cropModule.Publish("end_crop");
   } 

   [EventHandler] public void OnHarvest()
   {
      Console.WriteLine(today + " Harvesting Crop");
      HarvestType hdata = new HarvestType();
      hdata.Remove = 1.0;
      hdata.Height = 50.0;
      string currentPaddock;
      MySimulation.Get("currentPaddock", out currentPaddock);
      Component cropModule;
      if (currentPaddock != "")
         cropModule = (Component) MySimulation.LinkByName(currentPaddock + "." + crop);
      else 
         cropModule = (Component) MySimulation.ChildPaddocks[0].LinkByName(crop);

      cropModule.Publish("harvest", hdata);
   } 
   [Output, Description("Test whether the crop can be harvested")] public int canHarvest 
   {
      get 
      {
         string currentPaddock;
         MySimulation.Get("currentPaddock", out currentPaddock);

         string plantStatus = "";
         MySimulation.Get((currentPaddock != "" ? currentPaddock + "." : "") + crop + ".plant_status", out plantStatus);
         if (plantStatus == "out")
            return -1;

         string StageName = "";
         MySimulation.Get((currentPaddock != "" ? currentPaddock + "." : "") + crop + ".StageName", out StageName);
         if (StageName == "flowering")
            return 1;
         return 0;
      }
   }

}

using System;
using ModelFramework;
using CSGeneral;

public class Script
{
   [Param()] private string DefoliationDateString;
   [Param()] private double ResidualStem;
   [Param()] private double ResidualLeaf;
   [Param()] private string Crop2Defoliate;
   
   [Link()]
   public Paddock MyPaddock;
   [Input()] private DateTime Today;
   
   private double GreenLeaf = 0.0;
   private double GreenStem = 0.0;
   private double dlt_GreenLeaf = 0.0;
   private double dlt_GreenStem = 0.0;
   private double Grazed = 0.0;
   
   [Output()]
   private DateTime DefoliationDate;
   
   [EventHandler]
   public void OnInitialised()
   {
      DefoliationDate = DateUtility.GetDate(DefoliationDateString);

   }
      
   [EventHandler] public void OnPrepare()
   {
      if ((Today.Day == DefoliationDate.Day && Today.Month == DefoliationDate.Month))   
      {
         Grazed = 1;
         MyPaddock.Get(Crop2Defoliate + "." + "leafgreenwt", out GreenLeaf);
         MyPaddock.Get(Crop2Defoliate + "." + "stemgreenwt", out GreenStem);

         dlt_GreenLeaf = Math.Max(0, GreenLeaf - ResidualLeaf);
         dlt_GreenStem = Math.Max(0, GreenStem - ResidualStem);
 
         RemoveCropBiomassdmType GreenRemoveCropDmDm = new RemoveCropBiomassdmType();
         
         GreenRemoveCropDmDm.pool = "green";
         GreenRemoveCropDmDm.part = new string[]
            {"leaf","stem"};
         GreenRemoveCropDmDm.dlt = new double[] 
            {dlt_GreenLeaf, dlt_GreenStem};
         
         RemoveCropBiomassType MyRemoveCropDM = new RemoveCropBiomassType();
        
         MyRemoveCropDM.dm = new RemoveCropBiomassdmType[] { GreenRemoveCropDmDm };
         
         Component Crop = MyPaddock.LinkByName(Crop2Defoliate) as Component;
         Crop.Publish("remove_crop_biomass", MyRemoveCropDM);
         
         Console.WriteLine(Today.ToShortDateString() + " - " + "Plant1 Defoliation Rule Invoked");
         Console.WriteLine("          - " + Crop2Defoliate + " has been defoliated");
         Console.WriteLine("          - Removed " + dlt_GreenLeaf + " g/m2 of Leaf");
         Console.WriteLine("          - Removed " + dlt_GreenStem + " g/m2 of Stem");
         Console.WriteLine(" ");
      }
   }
}

using CSGeneral; 
using ModelFramework;
using System;

// Crop management: Fertilise on a fixed date
// Not Multi-paddock aware.
// This component DOES NOT require a trigger from the sequencer.

public class Script 
{
   [Link]  public Simulation MyPaddock;
   [Link]  public Fertiliser MyFertiliser;

   [Param] private string fert_date;
   [Param] private float fert_criteria;
   [Param] private float fert_amount;
   [Param] private string fert_type;

   [Input] private DateTime today; 
   
   [EventHandler] public void OnPrepare()
   {
      if (DateUtility.DatesEqual(fert_date, today))
      {
         float[] NO3 = new float[20];
         float[] NH4 = new float[20];
         MyPaddock.Get("no3", out NO3);
         MyPaddock.Get("nh4", out NH4);
         float N_topsoil = NO3[0] + NO3[1] + NH4[0] + NH4[1];
         if(N_topsoil < fert_criteria){
            MyFertiliser.Apply(fert_amount, 50, fert_type);
         }
      }
   }
}

using System;
using ModelFramework;
using CSGeneral;

public class Script 
{      
   [Link] Irrigation Irrigation; 
   [Input] DateTime today;
   [Input] double[] dul_dep;     //drained upper limit (field capacity) 
   [Input] double[] sw_dep;      //soil water
   [Input] double[] ll15_dep;    //15 bar lower limit
   [Param] int deficit;          //allowable soil water deficit
   [Param] double eff;           //irrigation efficiency
   [Param] string start;         //no irrigation will be applied before this date
   [Param] string end;           //no irrigation will be applied after this date
   [Param] int maxlayer;         //calculate soil water deficit to this layer (inclusive)
   [Param] int amount;
   [Output] double totalSWD;     //total soil water deficit for given layers

   private double[] DulDep;
   private double[] SwDep;
   private double[] LL15Dep;
   private double[] SWD;

   [EventHandler] public void OnInitialised()
   {
      DulDep = new double[dul_dep.Length];
      SwDep = new double[dul_dep.Length];
      LL15Dep = new double[dul_dep.Length];
      SWD = new double[dul_dep.Length];

      if (maxlayer > dul_dep.Length)
      {
         Console.WriteLine("Warning: max layer is greater than number of layers.");
         Console.WriteLine("Using full profile for soil water deficit calculations.");
         maxlayer = 0;
      }
   }

   // The following event handler will be called each day at the beginning of the day
   [EventHandler] public void OnPrepare()
   {
      for (int i = 0; i < (maxlayer > 0 ? maxlayer : dul_dep.Length); i++)
      {
         SWD[i] = dul_dep[i] - sw_dep[i];
      }
      totalSWD = MathUtility.Sum(SWD);

      //if the soil water deficit is higer than the allowed deficit, and today is within the irrigation window
      if (totalSWD > deficit && DateUtility.WithinDates(start, today, end))
      {
         IrrigationApplicationType data = new IrrigationApplicationType(); //using a type means we can use defaults for values we don't change
         data.Amount = amount == 0 ? deficit : amount;
         Irrigation.Set("irrigation_efficiency", eff);
         Irrigation.Apply(data);
      }
   }  
}

// Applies irrigation into the second soil layer after a crop has been sown(lateral inflow from furrow into adjacent beds)
// Irrigation is applied on a deficit defined by the number of soil layers (depth) and a deficit value [0-1]
// Water is inserted lateral using "inflow" as would be expected under furrow irrigation of a crop on raised beds
// Water applied is not reported as irrigation and requires a local variable to track applied irrigation
// Local variable returned and available for reporting is: applied_furrow_irrigation

using System;
using ModelFramework;
using CSGeneral;

public class Script 
{      
   [Link] Irrigation Irrigation; 
   [Input] DateTime today;
   [Input] double[] dul_dep;     //drained upper limit (field capacity) 
   [Input] double[] sw_dep;      //soil water
   [Input] double[] ll15_dep;    //15 bar lower limit
   [Input] int DaysAfterSowing;  // Days after sowing used as a trigger to start irrigation


   [Param] int asw_depth;        //available soilwater depth
   [Param] double crit_fr_asw;   //critical fraction of available soilwater before applying irrigation

   [Param] string start;         //no irrigation will be applied before this date
   [Param] string end;           //no irrigation will be applied after this date

   [Output] double[] inflow_lat;
   [Output] double applied_furrow_irrigation;

   // The following event handler will be called only once
   [EventHandler] public void OnInitialised()
   {
      inflow_lat = new double[dul_dep.Length];
      for(int i = 0; i < dul_dep.Length; i++)
      {
         inflow_lat[i] = 0;
      }      
   }
   // The following event handler will be called each day at the end of the day
   [EventHandler] public void OnPost()
   {
      double SW_now = 0;
      double CLL_now = 0;
      double SW_full = 0;
      double[] SW_add = new double[dul_dep.Length];

      for(int i = 0; i < asw_depth; i++)
      {
         SW_now = SW_now + sw_dep[i];
         CLL_now = CLL_now + ll15_dep[i];
         SW_full = SW_full + (dul_dep[i] - ll15_dep[i]);
         SW_add[i] = dul_dep[i] - sw_dep[i];
      }

      //if the soil water deficit is greater than the critical SW fraction and today is within the irrigation window
         if ((SW_now - CLL_now) <= (SW_full * crit_fr_asw) && DaysAfterSowing > 0 && DateUtility.WithinDates(start, today, end))

      {
         for(int i = 1; i < asw_depth; i++)
         {
            inflow_lat[i] = SW_add[i] + (SW_add[0] / (asw_depth - 1));
            applied_furrow_irrigation = applied_furrow_irrigation + inflow_lat[i];
      }
      }
      else
      {
         for(int i = 0; i < dul_dep.Length; i++)
         {
            inflow_lat[i] = 0;
            applied_furrow_irrigation = 0;
         }  
      }
   }  
}

using System;
using ModelFramework;

public class Script 
{      
   [Link] Irrigation Irrigation; 
   [Param] string ModuleName;    //the module that the event will come from
   [Param] string EventName;     //the event to look for
   [Param] double amount;        //amount of irrigation to apply (mm)
   [Param] double eff;           //irrigation efficiency

   // The following event handler will be called each day at the end of the day
   //[ModuleName] and [EventName] will be replaced by the values in the properties window before the script is run
   [EventHandler("[ModuleName].[EventName]")] public void Irrigate()
   {
      IrrigationApplicationType data = new IrrigationApplicationType();
      data.Amount = (int)amount;
      Irrigation.Set("irrigation_efficiency", eff);
      Irrigation.Apply(data);
      Console.WriteLine("Irrigating with " + amount + "mm due to " + ModuleName + "." + EventName + " event."); //write the event to the summary file
   }  
}

using System;
using ModelFramework;
using CSGeneral;

public class Script
{     
   [Link] Irrigation Irrigation;
   [Input] DateTime today;
   [Param] string[] irrigDatesStr;  //a string array holding the dates as text
   [Param] string sameDays;         //if yes, we ignore the year component
   [Param] float amount;           //amount of irrigation to apply (mm)
   [Param] float eff;              //irrigation efficiency

   private DateTime[] irrigDates;   //an array to hold the converted dates
   private bool irrigToday = false; //a variable to hold whether or not we irrigate today
   private int year;
   private string[] split;

   [EventHandler] public void OnInitialised()
   {
      //convert the date strings to APSIM dates
      irrigDates = new DateTime[irrigDatesStr.Length];
      for (int i = 0; i < irrigDates.Length ; i++)
      {
         split = irrigDatesStr[i].Split('-');
         if (split.Length == 3)
         {
            year = Convert.ToInt32(split[2]);
            irrigDates[i] = DateUtility.GetDate(irrigDatesStr[i], year);
         }
         else
            irrigDates[i] = DateUtility.GetDate(irrigDatesStr[i]);
      }
   }

   // The following event handler will be called each day at the start of the day
   [EventHandler] public void OnPrepare()
   {
      irrigToday = false;

      //for every date in our date array
      foreach (DateTime day in irrigDates)
      {
         if (sameDays.ToLower().Equals("yes") && day.Day.Equals(today.Day) && day.Month.Equals(today.Month))
            irrigToday = true;
         else if (day.Day.Equals(today.Day) && day.Month.Equals(today.Month) && day.Year.Equals(today.Year))
            irrigToday = true;  
      }

      if (irrigToday)
      {
         IrrigationApplicationType data = new IrrigationApplicationType();
         data.Amount = amount;
         Irrigation.Set("irrigation_efficiency", eff);
         Irrigation.Apply(data);
      }
   } 
}

using System;
using ModelFramework;
using System.Collections.Generic;
using CSGeneral;

public class Script 
{      
   [Link] Irrigation Irrigation; 
   [Input] DateTime today;
   [Input] double rain;
   [Param] int pastDays;         //check for rain over this period
   [Param] int wait;             //only consider irrigating if this many days have passed since last irrigation
   [Param] float amount;         //amount of rain
   [Param] float eff;            //irrigation efficiency
   [Param] string start;         //no irrigation will be applied before this date
   [Param] string end;           //no irrigation will be applied after this date
   [Param] float apply;          //apply mm of irrigation        
   [Output] double sum;

   private int dai = 0; //days after irrigation, set up a counter  
   private List<double> tracker = new List<double>();

   [EventHandler] public void OnPost()
   {
      tracker.Add(rain);
      if (tracker.Count > pastDays)
         tracker.RemoveAt(0);

      sum = 0;

      foreach(double d in tracker)
      {
         sum += d;
      }
      if (dai > wait && sum < amount && DateUtility.WithinDates(start, today, end))
         {
            IrrigationApplicationType data = new IrrigationApplicationType();
            data.Amount = apply;
            Irrigation.Set("irrigation_efficiency", eff);
            Irrigation.Apply(data);
            dai = 0; //reset the counter
         }
      dai++; //increase the counter by one
   }  
}

using System;
using ModelFramework;
using CSGeneral;

public class Script 
{      
   [Link] Irrigation Irrigation; 
   [Input] DateTime today;
   [Param] float amount;         //amount of irrigation to apply (mm)
   [Param] float eff;            //irrigation efficiency
   [Param] string start;         //no irrigation will be applied before this date
   [Param] string end;           //no irrigation will be applied after this date

   // The following event handler will be called each day at the start of the day
   [EventHandler] public void OnPrepare()
   {
      //if it's Monday and we're in the irrigation window
         if (today.DayOfWeek.ToString().Equals("Monday") && DateUtility.WithinDates(start, today, end))
         {
            IrrigationApplicationType data = new IrrigationApplicationType();
            data.Amount = amount;
            Irrigation.Set("irrigation_efficiency", eff);
            Irrigation.Apply(data);
         }
   }  
}

using System;
using ModelFramework;
using CSGeneral;

public class Script 
{      
   [Link] Paddock paddock;
   [Input] DateTime Today; 
   [Param] string reportDays;
   [Param] string module;

   private string[] days;
   private Outputfile output;

   [EventHandler] public void OnInitialised()
   {
      days = reportDays.Split(' ');
      output = (Outputfile) paddock.LinkByName(module);
   }

   [EventHandler] public void OnPost()
   {
      foreach (string s in days)
      {
         string[] split = s.Split('-');
         if (split.Length == 3) //contains a year
         {
            if(DateUtility.GetDate(split[0] + "-" + split[1], Convert.ToInt32(split[2])) == Today)
               output.DoOutput();
         }
         else
         {
            if (DateUtility.DatesEqual(s, Today))
               output.DoOutput();
         }
      }
   }  
}

using System; 
using System.Collections.Generic;
using ModelFramework; 
using CSGeneral;

// This captures and accumulates the states of several paddock variables
// to be reported on a single report line. 

// Multi-paddock aware.

public class Script 
{ 
   [Link] public Simulation MySimulation;

   // Cumulative Ep
   [Output, Description("The total transpiration since the last transition"), Units("mm")] public double TEp {
      get {setup(); string s = currentPaddock() + "_Ep"; return(me[s]);}
   }
   // Cumulative Drainage
   [Output, Description("The total drainage since the last transition"), Units("mm")] public double TDrainage {
      get {setup(); string s = currentPaddock() + "_drainage"; return(me[s]);}
   }
   // Cumulative Runoff
   [Output, Description("The total runoff since the last transition"), Units("mm")] public double TRunoff {
      get {setup(); string s = currentPaddock() + "_runoff"; return(me[s]);}
   }
   // Cumulative Es
   [Output, Description("The total soil evaporation since the last transition"), Units("mm")] public double TEs {
      get {setup(); string s = currentPaddock() + "_evaporation"; return(me[s]);}
   }
   // Cumulative fertiliser
   [Output, Description("The total fertiliser applied since the last transition"), Units("kg/ha")] public double applied_fert {
      get {setup(); string s = currentPaddock() + "_applied_fert"; return(me[s]);}
   }
   // Instantaneous sw at start
   [Output, Description("The soil water on the first day of a rotation"), Units("mm")] public double swStart {
      get {setup(); string s = currentPaddock() + "_swStart"; return(me[s]);}
   }
   // Instantaneous sw at end
   [Output, Description("The soil water on the last day of a rotation"), Units("mm")] public double swYesterday {
      get {setup(); string s = currentPaddock() + "_swYesterday"; return(me[s]);}
   }
   // Instantaneous no3 at start
   [Output, Description("The soil NO3 on the first day of a rotation"), Units("kg/ha")] public double no3Start {
      get {setup(); string s = currentPaddock() + "_no3Start"; return(me[s]);}
   }
   // Instantaneous no3 at end
   [Output, Description("The soil NO3 on the last day of a rotation"), Units("kg/ha")] public double no3Yesterday {
      get {setup(); string s = currentPaddock() + "_no3Yesterday"; return(me[s]);}
   }
   // Instantaneous carbon at end
   [Output, Description("The soil carbon on the last day of a rotation"), Units("kg/ha")] public double carbonYesterday {
      get {setup(); string s = currentPaddock() + "_carbon"; return(me[s]);}
   }
   // Instantaneous yield of all crops
   [Output, Description("Crop Yield"), Units("kg/ha")] public double yield_kgha {
      get{
         double yld2 = 0;
         Paddock p = (Paddock) MySimulation.LinkByName(currentPaddock());
         foreach (Component crop in p.Crops)
         {
            double yld = 0;
            crop.Get("yield", out yld);
            yld2 = yld2 + yld;
         }
         return yld2 ;}
   }
   // Instantaneous yield of all crops
   [Output, Description("Crop Yield"), Units("kg")] public double yield_kg {
      get{
         double yld2 = 0, area = 1.0;
         Paddock p = (Paddock) MySimulation.LinkByName(currentPaddock());
         MySimulation.Get(p.Name + ".area", out area);
         foreach (Component crop in p.Crops)
         {
            double yld = 0;
            crop.Get("yield", out yld);
            yld2 = yld2 + yld * area;
         }
         return yld2 ;}
   }
   // Instantaneous biomass of all crops
   [Output, Description("Crop biomass"), Units("kg/ha")] public double biomass_kgha {
      get{
         double biomass2 = 0;
         Paddock p = (Paddock) MySimulation.LinkByName(currentPaddock());
         foreach (Component crop in p.Crops)
         {
            double biomass = 0;
            crop.Get("biomass", out biomass);
            biomass2 = biomass2 + biomass;
         }
         return biomass2 ;}
   }
   // Instantaneous yield of all crops
   [Output, Description("Crop Biomass"), Units("kg")] public double biomass_kg {
      get{
         double biomass2 = 0, area = 1.0;
         Paddock p = (Paddock) MySimulation.LinkByName(currentPaddock());
         MySimulation.Get(p.Name + ".area", out area);
         foreach (Component crop in p.Crops)
         {
            double biomass = 0;
            crop.Get("biomass", out biomass);
            biomass2 = biomass2 + biomass * area;
         }
         return biomass2 ;}
   }
   // Instantaneous protein of all crops
   [Output, Description("Grain Protein"), Units("%")] public double protein {
      get{
         double pro2 = 0;
         Paddock p = (Paddock) MySimulation.LinkByName(currentPaddock());
         foreach (Component crop in p.Crops)
         {
            double pro = 0;
            crop.Get("grain_protein", out pro);
            pro2 = pro2 + pro;
         }
        
         return pro2 ;}
   }
   
   // Instantaneous N of all crops
   [Output, Description("Grain N"), Units("g/m2")] public double GrainN_gm2 {
      get{
         double GN2 = 0;
         Paddock p = (Paddock) MySimulation.LinkByName(currentPaddock());
         foreach (Component crop in p.Crops)
         {
            double GN = 0;
            if (crop.Get("grain_N", out GN))
               GN2 = GN2 + GN;
            else if (crop.Get("GrainGreenN", out GN))
               GN2 = GN2 + GN;
         }
         return GN2 ;}
   }
   
   
   [Output, Description("The state of the current paddock"), Units("")] public string paddockState {
      get {
         string cs = "";
         string cp = currentPaddock();
         MySimulation.Get(cp + "_state", out cs);
         return(cs);
      }
   }
   [Output, Description("The number of days in a rotation"), Units("")] public int daysInPhase = 0;
 
   private string currentPaddock() {
      string result = "";
      MySimulation.Get("currentPaddock", out result);
      if (result != "")
         return(result);
      else
         return(MySimulation.ChildPaddocks[0].Name);
   }


   // An accumulator for each thing in each paddock
   private Dictionary<string, double> me = new Dictionary<string, double>();

   private void setup() {
      if (me.Keys.Count == 0) { OnStart_Simulation(); }
   }
   
   [EventHandler] public void OnStart_Simulation() { 
      if (me.Keys.Count == 0) {
         foreach (Paddock paddock in MySimulation.ChildPaddocks) 
         {
            Component soil = (Component) paddock.LinkByType("soilwat");
            double [] swdep = new double[15];
            soil.Get("sw_dep", out swdep);
            me.Add(paddock.Name + "_swStart", MathUtility.Sum(swdep));
            me.Add(paddock.Name + "_swYesterday", MathUtility.Sum(swdep));
         
            Component soilN = (Component) paddock.LinkByType("soiln");
            double [] no3dep = new double[15];
            soilN.Get("no3", out no3dep);
            me.Add(paddock.Name + "_no3Start", MathUtility.Sum(no3dep));
            me.Add(paddock.Name + "_no3Yesterday", MathUtility.Sum(no3dep));
            me.Add(paddock.Name + "_Ep", 0);
            me.Add(paddock.Name + "_drainage", 0);
            me.Add(paddock.Name + "_runoff", 0);
            me.Add(paddock.Name + "_evaporation", 0);
            me.Add(paddock.Name + "_applied_fert", 0);
            double[] carbon = new double[15];
            soilN.Get("carbon_tot", out carbon);
            me.Add(paddock.Name + "_carbon", MathUtility.Sum(carbon));
         }
      }
   } 
   [EventHandler] public void OnTransition() {
      setup();   
      string s = currentPaddock();
      me[s + "_swStart"] = me[s+"_swYesterday"]; 
      me[s + "_no3Start"] = me[s + "_no3Yesterday"];
      me[s + "_Ep"] = 0;
      me[s + "_drainage"] = 0;
      me[s + "_runoff"] = 0;
      me[s + "_evaporation"] = 0;
      me[s + "_applied_fert"] = 0;
      me[s + "_carbon"] = 0;
      daysInPhase = 0;
   } 
   [EventHandler] public void OnPost() { 
      foreach (Paddock paddock in MySimulation.ChildPaddocks) 
      {
         Component soil = (Component) paddock.LinkByType("soilwat");
         Component soilN = (Component) paddock.LinkByType("soiln");
         Component fertiliser = (Component) paddock.LinkByType("fertiliser");
         double [] swdep = new double[15];
         soil.Get("sw_dep", out swdep);
         me[paddock.Name + "_swYesterday"] = MathUtility.Sum(swdep);

         double [] no3dep = new double[15];
         soilN.Get("no3", out no3dep);
         me[paddock.Name + "_no3Yesterday"] = MathUtility.Sum(no3dep);

         double drain = 0;
         soil.Get("drain", out drain);
         me[paddock.Name + "_drainage"] += drain;

         double runoff = 0;
         soil.Get("runoff", out runoff);
         me[paddock.Name + "_runoff"] += runoff;

         double es = 0;
         soil.Get("es", out es);
         me[paddock.Name + "_evaporation"] += es;

         double applied_fert = 0;
         fertiliser.Get("fertiliser", out applied_fert);
         me[paddock.Name + "_applied_fert"] += applied_fert;

         double[] carbon = new double[15];
         soilN.Get("carbon_tot", out carbon);
         me[paddock.Name + "_carbon"] = MathUtility.Sum(carbon);

         foreach (Component crop in paddock.Crops)
         {
            double Ep = 0.0;
            if(crop.Get("Ep", out Ep))
               me[paddock.Name + "_Ep"] += Ep;
         }
      }
      daysInPhase++;
   }
}

using System;
using ModelFramework;

//This manager sets up a cut/grazing rotation for AgPasture. All harvested material is carried away.
// The rotation is valid between two given dates only, these don't need to be bounded to the simulation dates (start date is set to simulation start date, if prior to it).
// Harvest is done on above ground standing DM, i.e. leaves plus stem/sheath (dead and green)
// This manager allows harvest on fixed intervals:
//   Cut is allowed at given days only (set by start rotation date and then at regular intervals);
//   Cut happens if existing DM is greater than ResidualDM;
//   Cut is only allowed on the date selected, 'Can_Cut' is set to "no" at the end of the day;
//   The amount is defined by either the residualDM (all above it is harvested) or a given amount is removed

//-->  Last uptaded in: 05/Oct/2015 ..................

public class Script
{
   //Parameter variables:
   [Param] private DateTime CutPasture_StartDate;  //Date to start the cutting rotation
   [Param] private DateTime CutPasture_EndDate;    //Date to end cutting rotation
   [Param] private int Interval2Cut;               //The interval between cuts (days)
   [Param] private string RemoveAmountType;        //The type of amount being defined (Setting DM amount to remove,Setting residual DM amount)
   [Param] private double HerbageAmount ;          //The DM amount (kg/ha, DM above ground)

   //Links and events:
   [Link] private Component MySelf;
   [Link] private AgPasture MyAgPasture;

   //Variables read from other modules
   [Input] private DateTime Today;     //Today's date

   //Outputs
   [Output][Units("")] public string Can_Cut;                  //Whether today is a harvesting day
   [Output][Units("")] public string Has_Cut;                  //Whether today's harvest was done
   [Output][Units("kg/ha")] public double harvested_dm;        //Pasture DM harvested today (kg/ha)
   [Output][Units("kgN/ha")] public double harvested_n;        //N harvested today
   [Output][Units("kg/ha")] public double removed_dm;          //DM removed from the paddock
   [Output][Units("kgN/ha")] public double removed_n;          //N removed from the paddock
   [Output][Units("kgN/ha")] public double returned_dm;        //DM returned to the paddock
   [Output][Units("kgN/ha")] public double returned_n;         //N returned to the paddock
   [Output][Units("kg/ha/day")] public double GrowthRateCalc;  //Average growth rate for this cut
   [Output][Units("days")] public int DaysAfterCut;            //Number of days after last harvest
   [Output][Units("kg/ha")] public double PreHarvestDM;        //Total standing DM (Leaf + Stem/Sheath) before cut
   [Output][Units("kg/ha")] public double PostHarvestDM;       //Total standing DM (Leaf + Stem/Sheath) after cut

   //Internal variables
   private double FracDM2Remove;       //The fraction of DM to be removed from the simulation (0-1)
   private bool CutIsAllowed;          //Whether cutting are allowed
   private double LowerThreshold;      //Minimum residual DM
   private double MinimumFraction;     //Minimum fraction of given amount that still triggers a harvest
   private int CutInterval;
   private double ResidualDM;
   private double AmountWanted;

   [EventHandler()]
   public void OnInitialised()
   {
      //Reset variables
      CutInterval = Interval2Cut;
      DaysAfterCut = CutInterval - 1;
      CutIsAllowed = false;
      Can_Cut = "no";
      Has_Cut = "no";
      LowerThreshold = 500;   // assume a minimum of 500kgDM/ha
      MinimumFraction = 0.1;  // assume that at least 10% of required amount should be available to trigger a harvest

      //write messages on summary file
      string myMessage1 = ", every " + CutInterval.ToString() + " days";
      string myMessage2 = "     remove ";
      if (RemoveAmountType.Contains("residual"))
      {
         ResidualDM = HerbageAmount;
         AmountWanted = 0.0;
         myMessage2 += "all biomass down to residual DM (" + ResidualDM.ToString("#0") + " kgDM/ha)";
      }
      else if (RemoveAmountType.Contains("remove"))
      {
         ResidualDM = LowerThreshold;
         AmountWanted = HerbageAmount;
         myMessage2 += "a given amount (" + AmountWanted.ToString("#0") + " kgDM/ha)";
      }
      Console.WriteLine("");
      Console.WriteLine("   Harvest rotation for AgPasture initialised");
      Console.WriteLine("     rotation rule valid between " + CutPasture_StartDate.ToString("dd/MM/yyyy") + " and " + CutPasture_EndDate.ToString("dd/MM/yyyy"));
      Console.WriteLine("     rotation type: harvest at regular intervals" + myMessage1);
      Console.WriteLine(myMessage2);
      Console.WriteLine("");
   }

   [EventHandler()]
   public void OnPrepare()
   {
      double Amount2Harvest = 0.0;
      double AmountHarvestable = 0.0;

      //Reset variables:
      Has_Cut = "no";
      harvested_dm = 0.0;
      harvested_n = 0.0;
      removed_dm = 0.0;
      removed_n = 0.0;
      returned_dm = 0.0;
      returned_n = 0.0;
      GrowthRateCalc = 0.0;
      DaysAfterCut = DaysAfterCut + 1;
      CutInterval = Interval2Cut;
      PreHarvestDM = MyAgPasture.StandingPlantWt;
      if (RemoveAmountType.Contains("residual"))
      {
         ResidualDM = HerbageAmount;
         AmountWanted = Math.Max(0.0, PreHarvestDM - ResidualDM);
      }
      else if (RemoveAmountType.Contains("remove"))
      {
         ResidualDM = LowerThreshold;
         AmountWanted = HerbageAmount;
      }

      //Decide whether cut is allowed:
      if ((Today.Date >= CutPasture_StartDate.Date) && (Today.Date <= CutPasture_EndDate.Date))
      {
         if (DaysAfterCut >= CutInterval)
         {
            CutIsAllowed = true;
            Can_Cut = "yes";
         }
         else
         {
            CutIsAllowed = false;
            Can_Cut = "no";
            //always reset CutIsAllowed
         }

         //Decide how much to cut:
         if (CutIsAllowed)
         {
            AmountHarvestable = Math.Max(0.0, PreHarvestDM - ResidualDM);
            if (AmountHarvestable >= AmountWanted * MinimumFraction)
               Amount2Harvest = Math.Min(AmountWanted, AmountHarvestable);
            else
               Amount2Harvest = AmountWanted;

            //Decide whether cut actually happens:
            if ((AmountHarvestable >= Amount2Harvest))
            {
               CutPasture(Amount2Harvest);
               //reset cut variables
               DaysAfterCut = 0;
               Has_Cut = "yes";
               GrowthRateCalc = harvested_dm / DaysAfterCut;
               PostHarvestDM = MyAgPasture.StandingPlantWt;
            }
         }
         else
         {
            Amount2Harvest = 0.0;
            AmountHarvestable = 0.0;
         }
      }
   }


   private void CutPasture(double Amount2Harvest)
   {
      // -- Cutting pasture ----------------------------------------------------------------------------

      GrazeType CutData = new GrazeType();
      CutData.sender = MySelf.Name;
      CutData.amount = (float) Amount2Harvest;
      CutData.type = "SetRemoveAmount";
      MyAgPasture.Graze(CutData);
      harvested_dm = MyAgPasture.HarvestWt;
      harvested_n = MyAgPasture.HarvestN;

      //Let user know (via summary) that a cut was made
      Console.WriteLine(Today.ToString("dd MMMM yyyy") + " (Day of year=" + Today.DayOfYear.ToString() + "), " + MySelf.Name + ":");
      Console.WriteLine("     Removing " + harvested_dm.ToString("#0") + "kg DM/ha, with " + harvested_n.ToString("#0.0") + "kg N/ha");
   }
}

using System;
using ModelFramework;

//This manager sets up a cut/grazing rotation for AgPasture. It also handles the return of harvested material, as dung and urine.
// The rotation is valid between two given dates only, these don't need to be bounded to the simulation dates (start date is set to simulation start date, if prior to it).
// Harvest is done on above ground standing DM, i.e. leaves plus stem/sheath (dead and green).
// This manager allows harvest on fixed intervals:
//   Cut is allowed at given days only (set by start rotation date and then at regular intervals);
//   Cut happens if amount to remove is set and/or if existing DM is greater than ResidualDM;
//   Cut is only allowed on the date selected, 'Can_Cut' is set to "no" at the end of the day;
//
// Removal of DM (carbon) is based on digestibility, while for N a fraction is removed
// The DM and N to be returned as dung are added to surfaceOM 'RuminantDung_PastureFed'.
// The partition of N between urine and dung is set by the user.
// N urine is added as urea fertiliser, distributed from surface down to  a given depth. The proportion per layer decreases linearly with depth.

//-->  Last uptaded in: 05/Oct/2015 ..................

public class Script
{
   //Parameter variables:
   [Param] private DateTime CutPasture_StartDate;  //Date to start the cutting rotation
   [Param] private DateTime CutPasture_EndDate;    //Date to end cutting rotation
   [Param] private int Interval2Cut;               //The interval between cuts, used if cut trigger type is by interval (days), (values will be interpolated using DoY)
   [Param] private string RemoveAmountType;        //The type of amount being defined (Setting DM amount to remove,Setting residual DM amount)
   [Param] private double HerbageAmount ;          //The DM amount (kg/ha, DM above ground)

   [Param] private double FracN2Remove;            //Fraction of the N in the material cut that is removed from simulation (0-1)
   [Param] private double UrineDepth;              //The depth down to which urine will be applied (mm), proportion applied per layer decrease with depth
   [Param] private double FracN2Dung;              //The fraction of returned N that goes into dung, if return is as dung & urine (0-1)

   //Links and events:
   [Link] private Component MySelf;
   [Link] private AgPasture MyAgPasture;
   [Link] private Fertiliser MyFertiliser;
   [Link] private SurfaceOM MySurfaceOM;

   //Variables read from other modules
   [Input] private DateTime Today;     //Today's date
   [Input] private double[] dlayer;    //Today's day of year
   private double LowerThreshold;      //Minimum residual DM
   private double MinimumFraction;     //Minimum fraction of given amount that still triggers a harvest
   private int CutInterval;
   private double ResidualDM;
   private double AmountWanted;

   //Outputs
   [Output][Units("")] public string Can_Cut;                  //Whether today is a harvesting day
   [Output][Units("")] public string Has_Cut;                  //Whether today's harvest was done
   [Output][Units("kg/ha")] public double harvested_dm;        //Pasture DM harvested today (kg/ha)
   [Output][Units("kgN/ha")] public double harvested_n;        //N harvested today
   [Output][Units("kg/ha")] public double removed_dm;          //DM removed from the paddock
   [Output][Units("kgN/ha")] public double removed_n;          //N removed from the paddock
   [Output][Units("kgN/ha")] public double returned_dm;        //DM returned to the paddock
   [Output][Units("kgN/ha")] public double returned_n;         //N returned to the paddock
   [Output][Units("kgN/ha")] public double NReturnedInDung;    //N returned as dung (SurfaceOM)
   [Output][Units("kgN/ha")] public double NReturnedInUrine;   //N returned as urine (urea)
   [Output][Units("kg/ha/day")] public double GrowthRateCalc;  //Average growth rate for this cut
   [Output][Units("days")] public int DaysAfterCut;            //Number of days after last harvest
   [Output][Units("kg/ha")] public double PreHarvestDM;        //Total standing DM (Leaf + Stem/Sheath) before cut
   [Output][Units("kg/ha")] public double PostHarvestDM;       //Total standing DM (Leaf + Stem/Sheath) after cut

   //Internal variables
   private double FracDM2Remove;       //The fraction of DM to be removed from the simulation (0-1)
   private bool CutIsAllowed;          //Whether cutting are allowed
   private double[] FracLayer;         //The fraction of urine to be applied in each layer

   [EventHandler()]
   public void OnInitialised()
   {
      //Reset variables
      CutInterval = Interval2Cut;
      DaysAfterCut = CutInterval - 1;
      CutIsAllowed = false;
      Can_Cut = "no";
      Has_Cut = "no";
      LowerThreshold = 500;   // assume a minimum of 500kgDM/ha
      MinimumFraction = 0.5;  // assume that at least 50% of required amount should be available to trigger a harvest

      //Check the fractions of DM ad N to remove
      FracDM2Remove = MyAgPasture.DefoliatedDigestibility;
      if ((FracN2Remove < 0.0) || (FracN2Remove > 1.0))
      {
         throw new Exception("Fraction of N to remove is out of bounds");
      }
      if ((FracN2Dung < 0.0) || (FracN2Dung > 1.0))
      {
         throw new Exception("Proportion of N to return as dung is out of bounds");
      }

      //compute the fraction of urine to be aplied per layer
      FracLayer = new double[dlayer.Length];
      double DepthFromSurface = 0.0;
      double dltZ = 0.0;
      for (int z = 0; z < dlayer.Length;z++)
      {
         if(DepthFromSurface < UrineDepth)
         {
            dltZ = Math.Min(dlayer[z], UrineDepth - DepthFromSurface);
            FracLayer[z] = 2 * dltZ * (1 - ((DepthFromSurface + 0.5 * dltZ) / UrineDepth)) / UrineDepth;
         }
         DepthFromSurface += dlayer[z];
      }

      //write messages on summary file
      string myMessage1 = ", every " + CutInterval.ToString() + " days";
      string myMessage2 = "     remove ";
      if (RemoveAmountType.Contains("residual"))
      {
         ResidualDM = HerbageAmount;
         AmountWanted = 0.0;
         myMessage2 += "all biomass down to residual DM (" + ResidualDM.ToString("#0") + " kgDM/ha)";
      }
      else if (RemoveAmountType.Contains("remove"))
      {
         ResidualDM = LowerThreshold;
         AmountWanted = HerbageAmount;
         myMessage2 += "a given amount (" + AmountWanted.ToString("#0") + " kgDM/ha)";
      }
      Console.WriteLine("");
      Console.WriteLine("   Harvest rotation for AgPasture initialised");
      Console.WriteLine("     rotation rule valid between " + CutPasture_StartDate.ToString("dd/MM/yyyy") + " and " + CutPasture_EndDate.ToString("dd/MM/yyyy"));
      Console.WriteLine("     rotation type: harvest at regular intervals" + myMessage1);
      Console.WriteLine(myMessage2);
      Console.WriteLine("     fraction of harvested DM to return as dung: based on herbage digestibility");
      myMessage2 = ((1.0 - FracN2Remove) * 100).ToString("#0.0") + "% (" + ((1.0 - FracN2Dung) * 100).ToString("#0.0") + "% of it as urine)";
      Console.WriteLine("     fraction of harvested N to return as dung & urine: " + myMessage2);
      Console.WriteLine("     proportion of urine N to be added in each layer:");
      myMessage1 = FracLayer[0].ToString("0.000");
      for (int z = 1;z < dlayer.Length;z++)
      {
         myMessage1 += ", " + FracLayer[z].ToString("0.000");
      }
      Console.WriteLine("      " + myMessage1);
      Console.WriteLine("");
   }

   [EventHandler()]
   public void OnPrepare()
   {
      double Amount2Harvest = 0.0;
      double AmountHarvestable = 0.0;

      //Reset variables:
      Has_Cut = "no";
      harvested_dm = 0.0;
      harvested_n = 0.0;
      removed_dm = 0.0;
      removed_n = 0.0;
      returned_dm = 0.0;
      returned_n = 0.0;
      GrowthRateCalc = 0.0;
      NReturnedInDung = 0.0;
      NReturnedInUrine = 0.0;
      DaysAfterCut = DaysAfterCut + 1;
      CutInterval = Interval2Cut;
      PreHarvestDM = MyAgPasture.StandingPlantWt;
      if (RemoveAmountType.Contains("residual"))
      {
         ResidualDM = HerbageAmount;
         AmountWanted = Math.Max(0.0, PreHarvestDM - ResidualDM);
      }
      else if (RemoveAmountType.Contains("remove"))
      {
         ResidualDM = LowerThreshold;
         AmountWanted = HerbageAmount;
      }

      //Decide whether cut is allowed:
      if ((Today.Date >= CutPasture_StartDate.Date) && (Today.Date <= CutPasture_EndDate.Date))
      {
         if (DaysAfterCut >= CutInterval)
         {
            CutIsAllowed = true;
            Can_Cut = "yes";
         }
         else
         {
            CutIsAllowed = false;
            Can_Cut = "no";
            //always reset CutIsAllowed
         }

         //Decide how much to cut:
         if (CutIsAllowed)
         {
            AmountHarvestable = Math.Max(0.0, PreHarvestDM - ResidualDM);
            if (AmountHarvestable >= AmountWanted * MinimumFraction)
               Amount2Harvest = Math.Min(AmountWanted, AmountHarvestable);
            else
               Amount2Harvest = AmountWanted;

            //Decide whether cut actually happens:
            if ((AmountHarvestable >= Amount2Harvest))
            {
               CutPasture(Amount2Harvest);
               HandleResidues();
               //reset cut variables
               DaysAfterCut = 0;
               Has_Cut = "yes";
               GrowthRateCalc = harvested_dm / DaysAfterCut;
               PostHarvestDM = MyAgPasture.StandingPlantWt;
            }
         }
         else
         {
            Amount2Harvest = 0.0;
            AmountHarvestable = 0.0;
         }
      }
   }


   private void CutPasture(double Amount2Harvest)
   {
      // -- Cutting pasture ----------------------------------------------------------------------------

      GrazeType CutData = new GrazeType();
      CutData.sender = MySelf.Name;
      CutData.amount = (float) Amount2Harvest;
      CutData.type = "SetRemoveAmount";
      MyAgPasture.Graze(CutData);
      harvested_dm = MyAgPasture.HarvestWt;
      harvested_n = MyAgPasture.HarvestN;

      //Let user know (via summary) that a cut was made
      Console.WriteLine(Today.ToString("dd MMMM yyyy") + " (Day of year=" + Today.DayOfYear.ToString() + "), " + MySelf.Name + ":");
      Console.WriteLine("     Removing " + harvested_dm.ToString("#0") + "kg DM/ha, with " + harvested_n.ToString("#0.0") + "kg N/ha");
   }


   private void HandleResidues()
   {
      // -- Residue management -------------------------------------------------------------------------

      FracDM2Remove = MyAgPasture.DefoliatedDigestibility;
      removed_dm = harvested_dm * FracDM2Remove;
      removed_n = harvested_n * FracN2Remove;
      returned_dm = harvested_dm - removed_dm;
      returned_n = harvested_n - removed_n;

      if (returned_dm < 0.0)
      {
         throw new Exception("Return_DM value is negative");
      }
      if (returned_n < 0.0)
      {
         throw new Exception("Return_N value is negative");
      }

      NReturnedInDung = returned_n * FracN2Dung;
      NReturnedInUrine = Math.Max(0.0, returned_n - (returned_n * FracN2Dung));

      if (NReturnedInDung > returned_n)
      {
         throw new Exception("Amount of N to return as dung is larger than total return, check proportion or CN ratio");
      }

      if ((returned_dm > 0.0) || (NReturnedInDung > 0.0))
      {
         BiomassRemovedType SOMData = new BiomassRemovedType();
         SOMData.crop_type = "RuminantDung_PastureFed";
         SOMData.dm_type = new string[] { SOMData.crop_type };
         SOMData.dlt_crop_dm = new float[] { (float) returned_dm };
         SOMData.dlt_dm_n = new float[] { (float) NReturnedInDung };
         SOMData.fraction_to_residue = new float[] { 1.0F };
         MySurfaceOM.BiomassRemoved(SOMData);
      }

      if (NReturnedInUrine > 0.0)
      {
         FertiliserApplicationType UrineData = new FertiliserApplicationType();
         UrineData.Amount = (float) NReturnedInUrine;
         UrineData.Depth = 10;   // mm
         UrineData.Type = "urea_n";
         MyFertiliser.Apply(UrineData);
      }
   }
}

using System;
using ModelFramework;

//This manager sets up a cut/grazing rotation for AgPasture. All harvested material is carried away.
// The rotation is valid between two given dates only, these don't need to be bounded to the simulation dates (start date is set to simulation start date, if prior to it).
// Harvest is done on above ground standing DM, i.e. leaves plus stem/sheath (dead and green)
// This manager allows harvest whenever a target standing DM is reached
// After triggered, harvest of a given amount will happen untill DM is bellow the LowerThreshold (i.e. residual DM);

//-->  Last uptaded in: 05/Oct/2015 ..................

public class Script
{
   //Parameter variables:
   [Param] private DateTime CutPasture_StartDate;  //Date to start the cutting rotation
   [Param] private DateTime CutPasture_EndDate;    //Date to end cutting rotation
   [Param] private double UpperThreshold;          //The standing biomass amount to trigger a cut(kg/ha, DM above ground)
   [Param] private double LowerThreshold;          //The minimum residual DM amount left after cut (kg/ha, DM above ground)
   [Param] private double Amount2Cut;              //The amount of DM to be removed at each cut, limited to residual DM (kg/ha, DM above ground)
   //  Note: If Amount2Cut is negative, all biomass down to residual DM is removed in a single day

   //Links and events:
   [Link] private Component MySelf;
   [Link] private AgPasture MyAgPasture;

   //Variables read from other modules
   [Input] private DateTime Today;     //Today's date

   //Outputs
   [Output][Units("")] public string Can_Cut;                  //Whether today is a harvesting day
   [Output][Units("")] public string Has_Cut;                  //Whether today's harvest was done
   [Output][Units("kg/ha")] public double harvested_dm;        //Pasture DM harvested today (kg/ha)
   [Output][Units("kgN/ha")] public double harvested_n;        //N harvested today
   [Output][Units("kg/ha")] public double removed_dm;          //DM removed from the paddock
   [Output][Units("kgN/ha")] public double removed_n;          //N removed from the paddock
   [Output][Units("kgN/ha")] public double returned_dm;        //DM returned to the paddock
   [Output][Units("kgN/ha")] public double returned_n;         //N returned to the paddock
   [Output][Units("kg/ha/day")] public double GrowthRateCalc;  //Average growth rate for this cut
   [Output][Units("days")] public int DaysAfterCut;            //Number of days after last harvest
   [Output][Units("kg/ha")] public double PreHarvestDM;        //Total standing DM (Leaf + Stem/Sheath) before cut
   [Output][Units("kg/ha")] public double PostHarvestDM;       //Total standing DM (Leaf + Stem/Sheath) after cut

   //Internal variables
   private double FracDM2Remove;       //The fraction of DM to be removed from the simulation (0-1)
   private bool CutIsAllowed;          //Whether cutting are allowed
   private double MinimumFraction;     //Minimum fraction of given amount that still triggers a harvest
   private double TriggerDM;
   private double ResidualDM;
   private double AmountWanted;

   [EventHandler()]
   public void OnInitialised()
   {
      //Reset variables
      DaysAfterCut = -1;
      CutIsAllowed = false;
      Can_Cut = "no";
      Has_Cut = "no";
      TriggerDM = UpperThreshold;
      ResidualDM = LowerThreshold;
      MinimumFraction = 0.5;  // assume that at least 50% of required amount should be available to trigger a harvest

      //write messages on summary file
      string myMessage1 = ", cut when DM reaches " + TriggerDM + " kgDM/ha";
      string myMessage2 = "     remove all biomass down to residual DM (" + ResidualDM.ToString("#0") + " kgDM/ha)";
      if (Amount2Cut > 0.0)
      {
         myMessage2 += ", " + Amount2Cut.ToString("#0.0") + "kg/ha per day";
      }
      Console.WriteLine("");
      Console.WriteLine("   Harvest rotation for AgPasture initialised");
      Console.WriteLine("     rotation rule valid between " + CutPasture_StartDate.ToString("dd/MM/yyyy") + " and " + CutPasture_EndDate.ToString("dd/MM/yyyy"));
      Console.WriteLine("     rotation type: target standing biomass" + myMessage1);
      Console.WriteLine(myMessage2);
      Console.WriteLine("");
   }

   [EventHandler()]
   public void OnPrepare()
   {
      double Amount2Harvest;
      double AmountHarvestable;

      //Reset variables:
      Has_Cut = "no";
      harvested_dm = 0.0;
      harvested_n = 0.0;
      removed_dm = 0.0;
      removed_n = 0.0;
      returned_dm = 0.0;
      returned_n = 0.0;
      GrowthRateCalc = 0.0;
      DaysAfterCut = DaysAfterCut + 1;
      PreHarvestDM = MyAgPasture.StandingPlantWt;
      TriggerDM = UpperThreshold;
      ResidualDM = LowerThreshold;
      if (Amount2Cut > 0.0)
      {
         AmountWanted = Amount2Cut;
      }
      else
      {
         AmountWanted = Math.Max(0.0, PreHarvestDM - ResidualDM);
      }

      //Decide whether cut is allowed:
      if ((Today.Date >= CutPasture_StartDate.Date) && (Today.Date <= CutPasture_EndDate.Date))
      {
         if (PreHarvestDM >= TriggerDM)
         {
            CutIsAllowed = true;
            Can_Cut = "yes";
         }

         //Decide how much to cut:
         if (CutIsAllowed)
         {
            AmountHarvestable = Math.Max(0.0, PreHarvestDM - ResidualDM);
            if (AmountHarvestable >= AmountWanted * MinimumFraction)
               Amount2Harvest = Math.Min(AmountWanted, AmountHarvestable);
            else
               Amount2Harvest = AmountWanted;

            //Decide whether cut actually happens:
            if ((AmountHarvestable >= Amount2Harvest))
            {
               CutPasture(Amount2Harvest);
               //reset cut variables
               DaysAfterCut = 0;
               Has_Cut = "yes";
               GrowthRateCalc = harvested_dm / DaysAfterCut;
               PostHarvestDM = MyAgPasture.StandingPlantWt;
               if (PostHarvestDM - ResidualDM <= AmountWanted * MinimumFraction)
               {
                  CutIsAllowed = false;
                  Can_Cut = "no";
               }
            }
         }
         else
         {
            Amount2Harvest = 0.0;
            AmountHarvestable = 0.0;
         }
      }
   }


   private void CutPasture(double Amount2Harvest)
   {
      // -- Cutting pasture ----------------------------------------------------------------------------

      GrazeType CutData = new GrazeType();
      CutData.sender = MySelf.Name;
      CutData.amount = (float) Amount2Harvest;
      CutData.type = "SetRemoveAmount";
      MyAgPasture.Graze(CutData);
      harvested_dm = MyAgPasture.HarvestWt;
      harvested_n = MyAgPasture.HarvestN;

      //Let user know (via summary) that a cut was made
      Console.WriteLine(Today.ToString("dd MMMM yyyy") + " (Day of year=" + Today.DayOfYear.ToString() + "), " + MySelf.Name + ":");
      Console.WriteLine("     Removing " + harvested_dm.ToString("#0") + "kg DM/ha, with " + harvested_n.ToString("#0.0") + "kg N/ha");
   }
}

using System;
using ModelFramework;

//This manager sets up a cut/grazing rotation for AgPasture. All harvested material is carried away.
// Harvest is done on above ground standing DM, i.e. leaves plus stem/sheath (dead and green).
// Harvest is allowed only at given dates, but only happens if there is enough biomass
// The amount to remove is defined by either the residualDM (all above it is harvested) or a given amount is removed
//
//-->  Last uptaded in: 05/Oct/2015 ..................

public class Script
{
   //Parameter variables:
   [Param] private string[] CutPasture_Dates;      //Dates to harvest
   [Param] private string RemoveAmountType;        //The type of amount being defined (Setting DM amount to remove,Setting residual DM amount)
   [Param] private double HerbageAmount ;          //The DM amount (kg/ha, DM above ground)

   //Links and events:
   [Link] private Component MySelf;
   [Link] private AgPasture MyAgPasture;

   //Variables read from other modules
   [Input] private DateTime Today;     //Today's date

   //Outputs
   [Output][Units("")] public string Can_Cut;                  //Whether today is a harvesting day
   [Output][Units("")] public string Has_Cut;                  //Whether today's harvest was done
   [Output][Units("kg/ha")] public double harvested_dm;        //Pasture DM harvested today (kg/ha)
   [Output][Units("kgN/ha")] public double harvested_n;        //N harvested today
   [Output][Units("kg/ha")] public double removed_dm;          //DM removed from the paddock
   [Output][Units("kgN/ha")] public double removed_n;          //N removed from the paddock
   [Output][Units("kgN/ha")] public double returned_dm;        //DM returned to the paddock
   [Output][Units("kgN/ha")] public double returned_n;         //N returned to the paddock
   [Output][Units("kg/ha/day")] public double GrowthRateCalc;  //Average growth rate for this cut
   [Output][Units("days")] public int DaysAfterCut;            //Number of days after last harvest
   [Output][Units("kg/ha")] public double PreHarvestDM;        //Total standing DM (Leaf + Stem/Sheath) before cut
   [Output][Units("kg/ha")] public double PostHarvestDM;       //Total standing DM (Leaf + Stem/Sheath) after cut

   //Internal variables
   private double FracDM2Remove;       //The fraction of DM to be removed from the simulation (0-1)
   private bool CutIsAllowed;          //Whether cutting are allowed
   private int NumCuts;                //The number of cuttings to be performed
   private int nCut;                   //Counter for the number of cuttings
   private DateTime[] GivenDates;      //Dates given for each cut
   private double LowerThreshold;      //Minimum residual DM
   private double MinimumFraction;     //Minimum fraction of given amount that still triggers a harvest
   private double ResidualDM;
   private double AmountWanted;

   [EventHandler()]
   public void OnInitialised()
   {
      //set up the array with dates
      NumCuts = CutPasture_Dates.Length;
      GivenDates = new DateTime[NumCuts];
      for (int i = 0;i < NumCuts;i++)
      {
         GivenDates[i] = DateTime.Parse(CutPasture_Dates[i]);
      }

      //Reset variables
      DaysAfterCut = -1;
      CutIsAllowed = false;
      Can_Cut = "no";
      Has_Cut = "no";
      nCut = 0;
      LowerThreshold = 500;   // assume a minimum of 500kgDM/ha
      MinimumFraction = 0.5;  // assume that at least 50% of required amount should be available to trigger a harvest

      //write messages on summary file
      string myMessage1 = "harvest triggered on given dates";
      string myMessage2 = "     remove ";
      if (RemoveAmountType.Contains("residual"))
      {
         ResidualDM = HerbageAmount;
         AmountWanted = 0.0;
         myMessage2 += "all biomass down to residual DM (" + ResidualDM.ToString("#0") + " kgDM/ha)";
      }
      else if (RemoveAmountType.Contains("remove"))
      {
         ResidualDM = LowerThreshold;
         AmountWanted = HerbageAmount;
         myMessage2 += "a given amount (" + AmountWanted.ToString("#0") + " kgDM/ha)";
      }
      Console.WriteLine("");
      Console.WriteLine("   Harvest rotation for AgPasture initialised");
      Console.WriteLine("     rotation rule valid between " + CutPasture_Dates[0] + " and " + CutPasture_Dates[NumCuts - 1]);
      Console.WriteLine("     rotation type: " + myMessage1);
      Console.WriteLine(myMessage2);
      Console.WriteLine("");
   }

   [EventHandler()]
   public void OnPrepare()
   {
      double Amount2Harvest = 0.0;
      double AmountHarvestable = 0.0;

      //Reset variables:
      Has_Cut = "no";
      harvested_dm = 0.0;
      harvested_n = 0.0;
      removed_dm = 0.0;
      removed_n = 0.0;
      returned_dm = 0.0;
      returned_n = 0.0;
      GrowthRateCalc = 0.0;
      DaysAfterCut = DaysAfterCut + 1;
      PreHarvestDM = MyAgPasture.StandingPlantWt;
      if (RemoveAmountType.Contains("residual"))
      {
         ResidualDM = HerbageAmount;
         AmountWanted = Math.Max(0.0, PreHarvestDM - ResidualDM);
      }
      else if (RemoveAmountType.Contains("remove"))
      {
         ResidualDM = LowerThreshold;
         AmountWanted = HerbageAmount;
      }

      //Decide whether cut is allowed:
      if (nCut < NumCuts)
      {
         if (Today.Date == GivenDates[nCut].Date)
         {
            CutIsAllowed = true;
            Can_Cut = "yes";
         }

         //Decide how much to cut:
         if (CutIsAllowed)
         {
            AmountHarvestable = Math.Max(0.0, PreHarvestDM - ResidualDM);
            if (AmountHarvestable >= AmountWanted * MinimumFraction)
               Amount2Harvest = Math.Min(AmountWanted, AmountHarvestable);
            else
               Amount2Harvest = AmountWanted;

            //Decide whether cut actually happens:
            if ((AmountHarvestable >= Amount2Harvest))
            {
               CutPasture(Amount2Harvest);
               //reset cut variables
               DaysAfterCut = 0;
               Has_Cut = "yes";
               GrowthRateCalc = harvested_dm / DaysAfterCut;
               PostHarvestDM = MyAgPasture.StandingPlantWt;
               CutIsAllowed = false;
               Can_Cut = "no";
               nCut += 1;
            }
         }
         else
         {
            Amount2Harvest = 0;
            AmountHarvestable = 0;
         }
      }
   }


   private void CutPasture(double Amount2Harvest)
   {
      // -- Cutting pasture ----------------------------------------------------------------------------

      GrazeType CutData = new GrazeType();
      CutData.sender = MySelf.Name;
      CutData.amount = (float) Amount2Harvest;
      CutData.type = "SetRemoveAmount";
      MyAgPasture.Graze(CutData);
      harvested_dm = MyAgPasture.HarvestWt;
      harvested_n = MyAgPasture.HarvestN;

      //Let user know (via summary) that a cut was made
      Console.WriteLine(Today.ToString("dd MMMM yyyy") + " (Day of year=" + Today.DayOfYear.ToString() + "), " + MySelf.Name + ":");
      Console.WriteLine("     Removing " + harvested_dm.ToString("#0") + "kg DM/ha, with " + harvested_n.ToString("#0.0") + "kg N/ha");
   }
}

using System;
using ModelFramework;

//This manager sets up a cut/grazing of AgPasture on given dates, for. It also handles the return of harvested material.
// Harvest is done on above ground standing DM, i.e. leaves plus stem/sheath (dead and green).
// All standing biomass will be removed down to residual DM (LowerThreshold);
//
// Removal of DM (carbon) is based on digestibility, while for N a fraction is removed
// The DM and N to be returned as dung are added to surfaceOM 'RuminantDung_PastureFed'.
// The partition of N between urine and dung is set by the user.
// N urine is added as urea fertiliser, distributed from surface down to  a given depth. The proportion per layer decreases linearly with depth.
//
//-->  Last uptaded in: 05/Oct/2015 ..................

public class Script
{
   //Parameter variables:
   [Param] private string[] CutPasture_Dates;      //Dates to harvest
   [Param] private string RemoveAmountType;        //The type of amount being defined (Setting DM amount to remove,Setting residual DM amount)
   [Param] private double HerbageAmount ;          //The DM amount (kg/ha, DM above ground)

   [Param] private double FracN2Remove;            //Fraction of the N in the material cut that is removed from simulation (0-1)
   [Param] private double UrineDepth;              //The depth down to which urine will be applied (mm), proportion applied per layer decrease with depth
   [Param] private double FracN2Dung;              //The fraction of returned N that goes into dung, if return is as dung & urine (0-1)

   //Links and events:
   [Link] private Component MySelf;
   [Link] private AgPasture MyAgPasture;
   [Link] private Fertiliser MyFertiliser;
   [Link] private SurfaceOM MySurfaceOM;

   //Variables read from other modules
   [Input] private DateTime Today;    //Today's date
   [Input] private double[] dlayer;   //Today's day of year

   //Outputs
   [Output][Units("")] public string Can_Cut;                  //Whether today is a harvesting day
   [Output][Units("")] public string Has_Cut;                  //Whether today's harvest was done
   [Output][Units("kg/ha")] public double harvested_dm;        //Pasture DM harvested today (kg/ha)
   [Output][Units("kgN/ha")] public double harvested_n;        //N harvested today
   [Output][Units("kg/ha")] public double removed_dm;          //DM removed from the paddock
   [Output][Units("kgN/ha")] public double removed_n;          //N removed from the paddock
   [Output][Units("kgN/ha")] public double returned_dm;        //DM returned to the paddock
   [Output][Units("kgN/ha")] public double returned_n;         //N returned to the paddock
   [Output][Units("kgN/ha")] public double NReturnedInDung;    //N returned as dung (SurfaceOM)
   [Output][Units("kgN/ha")] public double NReturnedInUrine;   //N returned as urine (urea)
   [Output][Units("kg/ha/day")] public double GrowthRateCalc;  //Average growth rate for this cut
   [Output][Units("days")] public int DaysAfterCut;            //Number of days after last harvest
   [Output][Units("kg/ha")] public double PreHarvestDM;        //Total standing DM (Leaf + Stem/Sheath) before cut
   [Output][Units("kg/ha")] public double PostHarvestDM;       //Total standing DM (Leaf + Stem/Sheath) after cut

   //Internal variables
   private double FracDM2Remove;       //The fraction of DM to be removed from the simulation (0-1)
   private bool CutIsAllowed;          //Whether cutting are allowed
   private double[] FracLayer;         //The fraction of urine to be applied in each layer
   private int NumCuts;                //The number of cuttings to be performed
   private int nCut;                   //Counter for the number of cuttings
   private DateTime[] GivenDates;      //Dates given for each cut
   private double LowerThreshold;      //Minimum residual DM
   private double MinimumFraction;     //Minimum fraction of given amount that still triggers a harvest
   private double ResidualDM;
   private double AmountWanted;

   [EventHandler()]
   public void OnInitialised()
   {
      //set up the array with dates
      NumCuts = CutPasture_Dates.Length;
      GivenDates = new DateTime[NumCuts];
      for (int i = 0;i < NumCuts;i++)
      {
         GivenDates[i] = DateTime.Parse(CutPasture_Dates[i]);
      }

      //Reset variables
      DaysAfterCut = -1;
      CutIsAllowed = false;
      Can_Cut = "no";
      Has_Cut = "no";
      nCut = 0;
      LowerThreshold = 500;   // assume a minimum of 500kgDM/ha
      MinimumFraction = 0.5;  // assume that at least 50% of required amount should be available to trigger a harvest

      //Check the fractions of DM ad N to remove
      FracDM2Remove = MyAgPasture.DefoliatedDigestibility;
      if ((FracN2Remove < 0.0) || (FracN2Remove > 1.0))
      {
         throw new Exception("Fraction of N to remove is out of bounds");
      }
      if ((FracN2Dung < 0.0) || (FracN2Dung > 1.0))
      {
         throw new Exception("Proportion of N to return as dung is out of bounds");
      }


      //compute the fraction of urine to be aplied per layer
      FracLayer = new double[dlayer.Length];
      double DepthFromSurface = 0.0;
      double dltZ = 0.0;
      for (int z = 0; z < dlayer.Length;z++)
      {
         if(DepthFromSurface < UrineDepth)
         {
            dltZ = Math.Min(dlayer[z], UrineDepth - DepthFromSurface);
            FracLayer[z] = 2 * dltZ * (1 - ((DepthFromSurface + 0.5 * dltZ) / UrineDepth)) / UrineDepth;
         }
         DepthFromSurface += dlayer[z];
      }

      //write messages on summary file
      string myMessage1 = "harvest triggered on given dates";
      string myMessage2 = "     remove ";
      if (RemoveAmountType.Contains("residual"))
      {
         ResidualDM = HerbageAmount;
         AmountWanted = 0.0;
         myMessage2 += "all biomass down to residual DM (" + ResidualDM.ToString("#0") + " kgDM/ha)";
      }
      else if (RemoveAmountType.Contains("remove"))
      {
         ResidualDM = LowerThreshold;
         AmountWanted = HerbageAmount;
         myMessage2 += "a given amount (" + AmountWanted.ToString("#0") + " kgDM/ha)";
      }
      Console.WriteLine("");
      Console.WriteLine("   Harvest rotation for AgPasture initialised");
      Console.WriteLine("     rotation rule valid between " + CutPasture_Dates[0] + " and " + CutPasture_Dates[NumCuts - 1]);
      Console.WriteLine("     rotation type: " + myMessage1);
      Console.WriteLine(myMessage2);
      Console.WriteLine("     fraction of harvested DM to return as dung: based on herbage digestibility");
      myMessage2 = ((1.0 - FracN2Remove) * 100).ToString("#0.0") + "% (" + ((1.0 - FracN2Dung) * 100).ToString("#0.0") + "% of it as urine)";
      Console.WriteLine("     fraction of harvested N to return as dung & urine: " + myMessage2);
      Console.WriteLine("     proportion of urine N to be added in each layer:");
      myMessage1 = FracLayer[0].ToString("0.000");
      for (int z = 1;z < dlayer.Length;z++)
      {
         myMessage1 += ", " + FracLayer[z].ToString("0.000");
      }
      Console.WriteLine("      " + myMessage1);
      Console.WriteLine("");
   }

   [EventHandler()]
   public void OnPrepare()
   {
      double Amount2Harvest = 0.0;
      double AmountHarvestable = 0.0;

      //Reset variables:
      Has_Cut = "no";
      harvested_dm = 0.0;
      harvested_n = 0.0;
      removed_dm = 0.0;
      removed_n = 0.0;
      returned_dm = 0.0;
      returned_n = 0.0;
      GrowthRateCalc = 0.0;
      NReturnedInDung = 0.0;
      NReturnedInUrine = 0.0;
      DaysAfterCut = DaysAfterCut + 1;
      PreHarvestDM = MyAgPasture.StandingPlantWt;
      if (RemoveAmountType.Contains("residual"))
      {
         ResidualDM = HerbageAmount;
         AmountWanted = Math.Max(0.0, PreHarvestDM - ResidualDM);
      }
      else if (RemoveAmountType.Contains("remove"))
      {
         ResidualDM = LowerThreshold;
         AmountWanted = HerbageAmount;
      }

      //Decide whether cut is allowed:
      if (nCut < NumCuts)
      {
         if (Today.Date == GivenDates[nCut].Date)
         {
            CutIsAllowed = true;
            Can_Cut = "yes";
         }

         //Decide how much to cut:
         if (CutIsAllowed)
         {
            AmountHarvestable = Math.Max(0.0, PreHarvestDM - ResidualDM);
            if (AmountHarvestable >= AmountWanted * MinimumFraction)
               Amount2Harvest = Math.Min(AmountWanted, AmountHarvestable);
            else
               Amount2Harvest = AmountWanted;

            //Decide whether cut actually happens:
            if ((AmountHarvestable >= Amount2Harvest))
            {
               CutPasture(Amount2Harvest);
               HandleResidues();
               //reset cut variables
               DaysAfterCut = 0;
               Has_Cut = "yes";
               GrowthRateCalc = harvested_dm / DaysAfterCut;
               PostHarvestDM = MyAgPasture.StandingPlantWt;
               CutIsAllowed = false;
               Can_Cut = "no";
               nCut += 1;
            }
         }
         else
         {
            Amount2Harvest = 0;
            AmountHarvestable = 0;
         }
      }
   }


   private void CutPasture(double Amount2Harvest)
   {
      // -- Cutting pasture ----------------------------------------------------------------------------

      GrazeType CutData = new GrazeType();
      CutData.sender = MySelf.Name;
      CutData.amount = (float) Amount2Harvest;
      CutData.type = "SetRemoveAmount";
      MyAgPasture.Graze(CutData);
      harvested_dm = MyAgPasture.HarvestWt;
      harvested_n = MyAgPasture.HarvestN;

      //Let user know (via summary) that a cut was made
      Console.WriteLine(Today.ToString("dd MMMM yyyy") + " (Day of year=" + Today.DayOfYear.ToString() + "), " + MySelf.Name + ":");
      Console.WriteLine("     Removing " + harvested_dm.ToString("#0") + "kg DM/ha, with " + harvested_n.ToString("#0.0") + "kg N/ha");
   }


   private void HandleResidues()
   {
      // -- Residue management -------------------------------------------------------------------------

      FracDM2Remove = MyAgPasture.DefoliatedDigestibility;
      removed_dm = harvested_dm * FracDM2Remove;
      removed_n = harvested_n * FracN2Remove;
      returned_dm = harvested_dm - removed_dm;
      returned_n = harvested_n - removed_n;

      if (returned_dm < 0.0)
      {
         throw new Exception("Return_DM value is negative");
      }
      if (returned_n < 0.0)
      {
         throw new Exception("Return_N value is negative");
      }

      NReturnedInDung = returned_n * FracN2Dung;
      NReturnedInUrine = Math.Max(0.0, returned_n - NReturnedInDung);

      if (NReturnedInDung > returned_n)
      {
         throw new Exception("Amount of N to return as dung is larger than total return, check proportion or CN ratio");
      }

      if ((returned_dm > 0.0) || (NReturnedInDung > 0.0))
      {
         BiomassRemovedType SOMData = new BiomassRemovedType();
         SOMData.crop_type = "RuminantDung_PastureFed";
         SOMData.dm_type = new string[] { SOMData.crop_type };
         SOMData.dlt_crop_dm = new float[] { (float) returned_dm };
         SOMData.dlt_dm_n = new float[] { (float) NReturnedInDung };
         SOMData.fraction_to_residue = new float[] { 1.0F };
         MySurfaceOM.BiomassRemoved(SOMData);
      }

      if (NReturnedInUrine > 0)
      {
         FertiliserApplicationType UrineData = new FertiliserApplicationType();
         UrineData.Amount = (float) NReturnedInUrine;
         UrineData.Depth = 10;   // mm
         UrineData.Type = "urea_n";
         MyFertiliser.Apply(UrineData);
      }
   }
}

using System;
using ModelFramework;

//This manager kills off AgPasture (stop it growing)

//-->  Last uptaded in: 01/Oct/2015 ..................

public class Script
{
   //Parameter variables:
   [Param] private DateTime KillPasture_Date;    //Date to kill off the pasture
   [Param] private string KillPasture_Name;      //Name of the instance of AgPasture to be killed off

   //Links and events:
   [Link] private Component MySelf;
   [Link] private Paddock MyPaddock;

   //Variables read from other modules
   [Input] private DateTime Today;   //Today's date

   private AgPasture MyAgPasture;

   [EventHandler()]
   public void OnInitialised()
   {
      Console.WriteLine("");
      Console.WriteLine("   Kill AgPasture initialised");
      Console.WriteLine("     " + KillPasture_Name + " will be killed off in " + KillPasture_Date.ToShortDateString());
      Console.WriteLine("");
   }

   [EventHandler()]
   public void OnPrepare()
   {
      //Decide whether to kill the pasture
      if (Today.Date == KillPasture_Date.Date)
      {
         MyAgPasture = (AgPasture) MyPaddock.LinkByName(KillPasture_Name);
         MyAgPasture.KillCrop(1.0F);
      }
   }
}

using System;
using ModelFramework;

//This manager sow AgPasture (allows it to grow)

