   if {[dateWithin [date1] [date2]] } {
      set sw1 [lindex [apsimGet sw] 0]
      if {$sw1 >= $sw1f_[crop] ||
          ( "[must_sow]" == "yes" && [dateIs '[date2]'] ) } {
          set ChooseNextCrop "yes"
          sow_[crop]
      }
